-- ----------------------------
-- Table structure for user_comments
-- ----------------------------
DROP TABLE IF EXISTS `user_comments`;
CREATE TABLE `user_comments`  (
  `id` int(4) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_id` int(10) NOT NULL COMMENT '作者主键',
  `session_id` int(10) NOT NULL COMMENT '用户主键',
  `article_id` int(10) NOT NULL COMMENT '文档主键',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文档内容',
  `reply_id` int(10) NOT NULL DEFAULT 0 COMMENT '回复ID',
  `status` tinyint(4) NOT NULL DEFAULT 1 COMMENT '状态1显示0隐藏',
  `create_time` int(10) NOT NULL COMMENT '创建时间',
  `update_time` int(10) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 31 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '文档评论表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of user_comments
-- ----------------------------
INSERT INTO `user_comments` VALUES (8, 4, 5, 43, '　　巧合的是，近日两国一群青年的交流，便引起了日媒注意。', 0, 1, 1549067878, 1549067878);
INSERT INTO `user_comments` VALUES (9, 4, 5, 43, '　　而面对中日之间一些遗留问题，樊小菊认为，双方应当在建立互信的基础上，继续从长远的角度考虑如何解决这些问题。', 0, 1, 1549068187, 1549068187);
INSERT INTO `user_comments` VALUES (10, 4, 5, 43, '　　中国现代国际关系研究院日本研究所执行所长樊小菊就此评论称，中日两国在维护自由贸易体制、反对贸易保护主义方面立场相近，加强合作才能互利共赢。', 0, 1, 1549068201, 1549068201);
INSERT INTO `user_comments` VALUES (11, 4, 5, 43, '　　目前，中日两国正在就这一新的拜年方式展开最后协调。', 0, 1, 1549068243, 1549068243);
INSERT INTO `user_comments` VALUES (12, 4, 5, 43, '  而日本参议院议员冈田广则引用“愚公移山”的故事，表示要以“愚公移山”的精神推动日中关系持续改善和发展，并将日中友好代代相传。', 0, 1, 1549068366, 1549068366);
INSERT INTO `user_comments` VALUES (13, 4, 4, 43, '　　安倍说，目前日中关系已回归正常轨道，希望继续密切两国领导人往来，深化两国各领域交流发展，推动两国关系上升至新的阶段。', 0, 1, 1549068408, 1549068408);
INSERT INTO `user_comments` VALUES (14, 5, 5, 55, '   肖战作为男主角张小凡的饰演者，在谈及对角色的理解时坦言：“这个角色十分真实，在他身上仿佛能看到自己生活的影子。”片中饰演陆雪琪的李沁则用“第一次尝试打戏', 0, 1, 1549070221, 1549070221);
INSERT INTO `user_comments` VALUES (15, 5, 0, 55, 'demo', 0, 1, 1578457700, 1578457700);
INSERT INTO `user_comments` VALUES (16, 5, 4, 47, 'demo', 0, 1, 1578458023, 1578458023);
INSERT INTO `user_comments` VALUES (17, 5, 4, 48, 'demo', 0, 1, 1578458207, 1578458207);
INSERT INTO `user_comments` VALUES (18, 4, 0, 45, '111', 0, 1, 1580789151, 1580789151);
INSERT INTO `user_comments` VALUES (19, 10, 10, 57, '医生妻子杨慧萍每天接待发热病人，量体温、抽血样、测样本，厚重的防护服一穿一天。', 0, 1, 1580983334, 1580983334);
INSERT INTO `user_comments` VALUES (20, 10, 10, 57, '立春了，春意渐浓，寒气也在逐渐散去。叶力云夫妻和孩子约好，这场仗打赢后，全家人一起去踏青。', 0, 1, 1580983434, 1580983434);
INSERT INTO `user_comments` VALUES (21, 10, 10, 57, '为确保不漏一车一人，做好疫情防控工作，执勤人员在寒风中通宵达旦地坚守收费站出口。', 0, 1, 1580983979, 1580983979);
INSERT INTO `user_comments` VALUES (22, 10, 10, 57, '疫情防控期间，公交地铁乘客少了许多，但通往医院的线路却越发显得重要。', 0, 1, 1580984030, 1580984030);
INSERT INTO `user_comments` VALUES (23, 10, 10, 57, '“我们坚守岗位，请大家放心！”——抗“疫”一线员工群像扫描\r\n作者:admin   发布时间:2020年02月06日   阅读量:41  \r\n\r\n　新华社南昌2月5日电 题：“我们坚守岗位，请大家放心！”——抗“疫”一线员工群像扫描\r\n\r\n　　新华社记者熊家林、姚子云\r\n\r\n　　“请下车量一下体温。”在沪昆高速经楼收费站，江西省', 0, 1, 1580995122, 1580995122);
INSERT INTO `user_comments` VALUES (24, 10, 0, 57, '疫情防控期间，公交地铁乘客少了许多，但通往医院的线路却越发显得重要。', 0, 1, 1580996535, 1580996535);
INSERT INTO `user_comments` VALUES (25, 10, 10, 53, '　　这次年度特刊的封面上，有中国科学院院长王贻芳，一级大法官沈德咏，中国乒乓球协会主席刘国梁，国际货币基金组织的全球副总裁朱民，故宫博物院院长单霁翔', 0, 1, 1580998810, 1580998810);
INSERT INTO `user_comments` VALUES (26, 10, 10, 53, '\r\n　　就让偶像和各界楷模站在一起接受荣光吧，他们已经粉丝满天下，赚走了其他行业一辈子都见不到的钱。现在，连英雄们的宴会，偶像也要分一杯羹了。', 0, 1, 1580999032, 1580999032);
INSERT INTO `user_comments` VALUES (27, 10, 10, 53, '“一切公众话语日渐以娱乐的方式出现，并成为一种文化精神。我们的政治、宗教、新闻、体育、教育和商业都心甘情愿地成为娱乐的附庸，毫无怨言，甚至无声无息，其结果是我们成了一个娱乐至死的物种。”\r\n　　——尼尔·波兹曼《娱乐至死》', 0, 1, 1580999121, 1580999121);
INSERT INTO `user_comments` VALUES (28, 10, 10, 53, '\r\n　　以前我们害怕孩子天天看娱乐节目，去学唱歌跳舞泡妞，不好好读书了；可现在，我们真的要害怕，孩子回家告诉你：读书是没有用的，好看才能成人生赢家。', 0, 1, 1580999186, 1580999186);
INSERT INTO `user_comments` VALUES (29, 10, 10, 53, '娱乐圈的存在就是为了给大家讨乐子。\r\n　　大家都不好好读书，没什么文化、学问，相互吹捧，\r\n　　然后有一点事不对了，就各自站队，拉集团，胡搞。\r\n', 0, 1, 1580999217, 1580999217);
INSERT INTO `user_comments` VALUES (30, 5, 10, 46, '根据之前公开的消息《EVA新剧场版:▕┃》将于2020年上映。', 0, 1, 1581131923, 1581131923);

